// MCP Server Implementation
const requestData = $json.body || {};
const { method, params } = requestData;

// Available tools
const tools = [
  {
    name: 'fetch_notices',
    description: 'Busca editais de licitação com filtros opcionais',
    inputSchema: {
      type: 'object',
      properties: {
        query: { type: 'string', description: 'Termo de busca' },
        limit: { type: 'number', description: 'Limite de resultados', default: 10 },
        status: { type: 'string', description: 'Status do edital' }
      }
    }
  },
  {
    name: 'risk_classifier',
    description: 'Classifica o risco de um edital de licitação',
    inputSchema: {
      type: 'object',
      properties: {
        notice_content: { type: 'string', description: 'Conteúdo do edital' },
        notice_id: { type: 'string', description: 'ID do edital' }
      },
      required: ['notice_content']
    }
  }
];

// MCP Protocol Handler
if (method === 'tools/list') {
  return {
    tools: tools
  };
}

if (method === 'tools/call') {
  const { name: toolName, arguments: toolArgs } = params;

  try {
    let result;

    switch (toolName) {
      case 'fetch_notices':
        result = {
          notices: [
            {
              id: '1',
              title: 'Edital de Exemplo',
              description: 'Descrição do edital',
              status: 'active',
              deadline: '2024-02-15',
              value: 100000
            }
          ],
          total: 1
        };
        break;

      case 'risk_classifier':
        const riskScore = Math.random();
        result = {
          risk_level: riskScore > 0.7 ? 'high' : riskScore > 0.4 ? 'medium' : 'low',
          risk_score: riskScore,
          risk_factors: ['Prazo curto', 'Documentação complexa'],
          recommendations: ['Revisar documentação', 'Preparar equipe']
        };
        break;

      default:
        throw new Error(`Tool '${toolName}' not found`);
    }

    return {
      content: [{
        type: 'text',
        text: JSON.stringify(result, null, 2)
      }]
    };

  } catch (error) {
    return {
      isError: true,
      content: [{
        type: 'text',
        text: `Error executing tool '${toolName}': ${error.message}`
      }]
    };
  }
}

// Handle other MCP methods
if (method === 'initialize') {
  return {
    protocolVersion: '2024-11-05',
    capabilities: {
      tools: {}
    },
    serverInfo: {
      name: 'SIBAL MCP Server',
      version: '1.0.0'
    }
  };
}

// Default response for unknown methods
return {
  error: {
    code: -32601,
    message: `Method '${method}' not found`
  }
};
